import React from 'react';
import { useForm } from 'react-hook-form';
import * as yup from 'yup';
import useResolver from "../../../hooks/useResolver";
import VisualForm from "./VisualForm";
import { useEffect } from "react/cjs/react.production.min";

const Form = () => {
    const formSchema = yup.object().shape({
        name: yup.string().required('Name is required'),
        email: yup
            .string()
            .required('Email is required')
            .email('It is not email'),
        message: yup.string().required('Message is required'),
    });

    const resolver = useResolver(formSchema);
    const {
        register,
        handleSubmit,
        errors,
        reset,
        watch,
        setValue,
        clearErrors,
    } = useForm({
        mode: 'onSubmit',
        resolver,
    });

    const name = watch('name');
    const email = watch('email');
    const message = watch('message');

    useEffect(() => {
        if (errors.email && email.trim()) {
            clearErrors('email');
        }
        if (errors.name && name.trim()) {
            clearErrors('name');
        }
        if (errors.message && message.trim()) {
            clearErrors('message');
        }
    }, [name, email, message]);

    useEffect(() => {
        if (errors.name || errors.message || errors.email) {
            if (errors.name) {
                setValue('name', '');
            }
            if (errors.message) {
                setValue('message', '');
            }
            if (errors.email) {
                setValue('email', '');
            }
        }
    }, [errors.name, errors.message, errors.email]);

    const submit = data => {
        console.info(data)
    }

    return (
        <VisualForm
            {...errors}
            handleSubmit={handleSubmit}
            register={register}
            submit={submit}
        />
    );
};

export default Form;
